                placeholder="Seu e-mail"
                labelText="E-mail"
                isValid={state.isEmailValid}
                regExp={emailRegExp}/>

            <ValidationInput 
                value={state.password}
                onChange={text => handlePasswordChange(text)}
                placeholder="Sua senha"
                labelText="Senha"
                isValid={state.isPasswordValid}
                regExp={emailRegExp}/>
            <Button text="Login" />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
