import React, { useEffect, useRef, useState } from "react";
import { TextInput, View, Text, StyleSheet } from "react-native";
import { AntDesign } from '@expo/vector-icons';
import colors from "../theme/colors";
import size from "../theme/fonts";

export function ValidationInput({labelText, isValid, ...restprops}) {      
    return (
        <>
            <Text style={styles.label}>{props.labelText}</Text>
            <View style={styles.containerInput}>            
                <TextInput 
                    {...props}
                    style={styles.input}  
                />                  
                { props.isValid && <AntDesign name="checkcircle" style={styles.icon} size={24} color={colors.check} /> }
            </View>
        </>
    );
}

const styles = StyleSheet.create({
    containerInput: {        
        flexDirection: "row",
        alignItems: "center",
        padding: 10,
        borderWidth: 1,
        borderRadius: 4,
        marginBottom: 20
    },
    label: {
        alignSelf: "flex-start",
        fontSize: size.label,

    },
    input: {
        backgroundColor: "white",
        fontSize: size.input,
        flex: 1,        
    },
    icon: {                
        paddingRight: 5
    }
});
